(10, 1, '2018-11-09 01:45:26', '2018-11-09 01:45:26', '<code>\r\n       <?php $blogusers = get_users(\'blog_id=0\');\r\n	        $homeurl = home_url();\r\n	        foreach ($blogusers as $user)\r\n	        {   if($user->user_nicename!=\'root\'){\r\n	            $url = $homeurl.\'/\'.$user->user_nicename;\r\n		    echo \"test\";				         \r\n	        }\r\n?>', 'Members', '', 'inherit', 'closed', 'closed', '', '7-revision-v1', '', '', '2018-11-09 01:45:26', '2018-11-09 01:45:26', '', 7, 'http://localhost/ISIR/amac/2018/11/09/7-revision-v1/', 0, 'revision', '', 0),
