INSERT INTO `isir_44_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 2, '2018-11-08 21:45:48', '2018-11-08 21:45:48', 'Bienvenue sur <a href=\"http://localhost/ISIR/\">ISIR</a>. Vous lisez votre premier article. Modifiez-le ou supprimez-le, puis commencez à publier !', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2018-11-08 21:45:48', '2018-11-08 21:45:48', '', 0, 'http://localhost/ISIR/micromanipulation-assistee/?p=1', 0, 'post', '', 1),
(2, 2, '2018-11-08 21:45:48', '2018-11-08 21:45:48', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/ISIR/micromanipulation-assistee/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2018-11-08 21:45:48', '2018-11-08 21:45:48', '', 0, 'http://localhost/ISIR/micromanipulation-assistee/?page_id=2', 0, 'page', '', 0),
(3, 1, '2018-11-08 23:25:37', '2018-11-08 23:25:37', '<p>This page is used by LoginPress to preview the login page in the Customizer.</p>', 'LoginPress', '', 'publish', 'closed', 'closed', '', 'loginpress', '', '', '2018-11-08 23:25:37', '2018-11-08 23:25:37', '', 0, 'http://localhost/ISIR/micromanipulation-assistee/loginpress/', 0, 'page', '', 0);

