INSERT INTO `isir_59_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 2, '2018-11-09 00:25:14', '2018-11-09 00:25:14', 'Bienvenue sur <a href=\"http://localhost/ISIR/\">ISIR</a>. Vous lisez votre premier article. Modifiez-le ou supprimez-le, puis commencez à publier !', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2018-11-09 00:25:14', '2018-11-09 00:25:14', '', 0, 'http://localhost/ISIR/ziyi/?p=1', 0, 'post', '', 1),
(2, 2, '2018-11-09 00:25:14', '2018-11-09 00:25:14', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/ISIR/ziyi/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2018-11-09 00:25:14', '2018-11-09 00:25:14', '', 0, 'http://localhost/ISIR/ziyi/?page_id=2', 0, 'page', '', 0),
(3, 1, '2018-11-09 00:29:28', '2018-11-09 00:29:28', '<p>This page is used by LoginPress to preview the login page in the Customizer.</p>', 'LoginPress', '', 'publish', 'closed', 'closed', '', 'loginpress', '', '', '2018-11-09 00:29:28', '2018-11-09 00:29:28', '', 0, 'http://localhost/ISIR/ziyi/loginpress/', 0, 'page', '', 0),
(4, 1, '2018-11-09 00:35:55', '2018-11-09 00:35:55', '{\n    \"Author::avatar_method\": {\n        \"value\": \"gravatar\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-11-09 00:35:55\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '7b661a95-241f-44e4-aba9-d03ed62cd84a', '', '', '2018-11-09 00:35:55', '2018-11-09 00:35:55', '', 0, 'http://localhost/ISIR/ziyi/2018/11/09/7b661a95-241f-44e4-aba9-d03ed62cd84a/', 0, 'customize_changeset', '', 0),
(5, 1, '2018-11-09 00:40:19', '2018-11-09 00:40:19', '', 'bg', '', 'inherit', 'open', 'closed', '', 'bg', '', '', '2018-11-09 00:40:19', '2018-11-09 00:40:19', '', 0, 'http://localhost/ISIR/ziyi/wp-content/uploads/sites/59/2018/11/bg.jpg', 0, 'attachment', 'image/jpeg', 0),
(6, 1, '2018-11-09 00:40:22', '2018-11-09 00:40:22', '{\n    \"loginpress_customization[setting_background]\": {\n        \"value\": \"http://localhost/ISIR/ziyi/wp-content/uploads/sites/59/2018/11/bg.jpg\",\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-11-09 00:40:22\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'e116ce27-337a-448b-a9c0-6e4e319c1616', '', '', '2018-11-09 00:40:22', '2018-11-09 00:40:22', '', 0, 'http://localhost/ISIR/ziyi/2018/11/09/e116ce27-337a-448b-a9c0-6e4e319c1616/', 0, 'customize_changeset', '', 0);

